import {css, html, LitElement} from 'lit';
import {styleMap} from 'lit-html/directives/style-map';
import {customElement} from 'lit/decorators';
import {property} from 'lit/decorators.js';

@customElement('gd-mouse')
export class Cursor extends LitElement {

  static styles = css`
    .mouse {
      --mouse-size: 21px;
      position: fixed;
      width: var(--mouse-size);
      height: var(--mouse-size);
      border-radius: var(--mouse-size);
      background-size: var(--mouse-size);
      background-image: url('data:image/svg+xml;base64,');
    }
  `;

  @property()
  top: '100%';

  @property()
  left: '100%';

  render() {
    return html`
      <div class="mouse" style=${styleMap({ top: this.top, left: this.left})}></div>`;
  }

}
